import React, { useState } from 'react'

const ERROR_IMG_SRC =
  'data:image/svg+xml;base64,'

interface ImageWithFallbackProps extends React.ImgHTMLAttributes<HTMLImageElement> {
  fallbackSrc?: string;
}

export function ImageWithFallback(props: ImageWithFallbackProps) {
  const [didError, setDidError] = useState(false)
  const [fallbackError, setFallbackError] = useState(false)

  const handleError = () => {
    if (!didError && props.fallbackSrc) {
      setDidError(true)
    } else {
      setFallbackError(true)
    }
  }

  const { src, fallbackSrc, alt, style, className, ...rest } = props

  // Determine which image source to use
  const imageSrc = didError && fallbackSrc ? fallbackSrc : src

  return fallbackError ? (
    <div
      className={`inline-block bg-gray-100 text-center align-middle ${className ?? ''}`}
      style={style}
    >
      <div className="flex items-center justify-center w-full h-full">
        <img src={ERROR_IMG_SRC} alt="Error loading image" {...rest} data-original-url={src} />
      </div>
    </div>
  ) : (
    <img src={imageSrc} alt={alt} className={className} style={style} {...rest} onError={handleError} />
  )
}
